<?phprequire_once 'dbh.class.php';// model classclass ProfileInfo extends Dbh{    protected function getProfileInfo(string $user_id)    {        $stmt = $this->connect()->prepare('SELECT * FROM profiles WHERE users_id = ?;');        if (!$stmt->execute(array($user_id))) {            $stmt = null;            header('location: ../profile.php?error=stmtfailed');            exit();        }        if ($stmt->rowCount() == 0) {            $stmt = null;            header('location: ../profile.php?error=noprofile');            exit();        }        $profileData = $stmt->fetchAll(PDO::FETCH_ASSOC);        return $profileData;    }    protected function setNewProfileInfo(string $profileAbout, string $profileTitle, string $profileText, string $user_id, string|bool $filePath)    {        if ($filePath == false) {            $stmt = $this->connect()->prepare('UPDATE profiles SET profiles_about = ?, profiles_intro_title = ?, profiles_intro_text = ? WHERE users_id = ?;');            if (!$stmt->execute(array($profileAbout, $profileTitle, $profileText, $user_id))) {                $stmt = null;                header('location: ../profile.php?error=stmtfailed');                exit();            }        } else {            $stmt = $this->connect()->prepare('UPDATE profiles SET profiles_about = ?, profiles_intro_title = ?, profiles_intro_text = ?, profile_image = ? WHERE users_id = ?;');            if (!$stmt->execute(array($profileAbout, $profileTitle, $profileText, $filePath, $user_id))) {                $stmt = null;                header('location: ../profile.php?error=stmtfailed');                exit();            }        }        $stmt = null;    }    protected function setProfileInfo(string $profileAbout, string $profileTitle, string $profileText, string $user_id)    {        $profileImage = 'img/seraphim.webp';        $stmt = $this->connect()->prepare('INSERT INTO profiles (profiles_about, profiles_intro_title, profiles_intro_text, users_id, profile_image) VALUES (?, ?, ?, ?, ?);');        if (!$stmt->execute(array($profileAbout, $profileTitle, $profileText, $user_id, $profileImage))) {            $stmt = null;            header('location: ../profile.php?error=stmtfailed');            exit();        }        $stmt = null;    }}